tables.table_schema:
CREATE TABLE parent_involvement (
    parent_id INT PRIMARY KEY,
    activity_name VARCHAR(255),
    involvement_level INT
);

CREATE TABLE volunteer_programs (
    program_id INT PRIMARY KEY,
    program_name VARCHAR(255),
    program_description TEXT
);

CREATE TABLE parent_feedback (
    feedback_id INT PRIMARY KEY,
    parent_id INT,
    program_id INT,
    rating INT
);

CREATE TABLE parent_involvement (
 id INT PRIMARY KEY,
 parent_name VARCHAR(50) NOT NULL,
 involvement_level VARCHAR(50) NOT NULL
);

CREATE TABLE extracurricular_activities (
 id INT PRIMARY KEY,
 activity_name VARCHAR(50) NOT NULL,
 description TEXT
);

CREATE TABLE school_programming (
 programming_id INT PRIMARY KEY,
 program_name VARCHAR(50) NOT NULL,
 parent_involvement_goal INT
);

CREATE TABLE parent_involvement (
   id INT PRIMARY KEY,
   parent_name VARCHAR(50),
   activity_name VARCHAR(50) NOT NULL,
   involvement_level INT CHECK (involvement_level >= 0 AND involvement_level <= 10)
);

CREATE TABLE activity (
   activity_id INT PRIMARY KEY,
   activity_name VARCHAR(50) UNIQUE
);

CREATE TABLE parent (
   parent_id INT PRIMARY KEY,
   parent_name VARCHAR(50) UNIQUE
);

CREATE TABLE volunteer_group (
   group_id INT PRIMARY KEY,
   group_name VARCHAR(50) NOT NULL
);

CREATE TABLE parent_activity_relation (
   relation_id INT PRIMARY KEY,
   parent_id INT REFERENCES parent(parent_id),
   activity_id INT REFERENCES activity(activity_id),
   involvement_level INT CHECK (involvement_level >= 0 AND involvement_level <= 10)
);
