tables.table_schema:
CREATE TABLE BiologyGenetics (
  student_id INT,
  student_name VARCHAR(50),
  genetic_data TEXT,
  inherited_traits TEXT
);

CREATE TABLE student_queries (
    id INT PRIMARY KEY,
    student_id INT,
    query_text TEXT,
    creation_date TIMESTAMP
);

CREATE TABLE students (
  student_id INT PRIMARY KEY,
  student_name VARCHAR(50),
  major VARCHAR(50),
  year_of_study INT
);

CREATE TABLE Students (
    student_id INT PRIMARY KEY,
    student_name VARCHAR(50) NOT NULL,
    student_gender CHAR(1),
    student_grade INT
);

CREATE TABLE Courses (
    course_id INT PRIMARY KEY,
    course_name VARCHAR(50) NOT NULL,
    course_description TEXT
);

CREATE TABLE GeneticData (
    data_id INT PRIMARY KEY,
    student_id INT,
    course_id INT,
    genetic_data JSONB
);

CREATE TABLE Traits (
    trait_id INT PRIMARY KEY,
    trait_name VARCHAR(50) NOT NULL,
    trait_description TEXT
);

CREATE TABLE InheritedTraits (
    inherited_trait_id INT PRIMARY KEY,
    student_id INT,
    trait_id INT,
    trait_value VARCHAR(50)
);
