CREATE TABLE materials (
  material_id INT PRIMARY KEY,
  title VARCHAR(255),
  author VARCHAR(255),
  resource_link VARCHAR(255)
);

CREATE TABLE categories (
  category_id INT PRIMARY KEY,
  category_name VARCHAR(255)
);

CREATE TABLE curriculum_materials (
  material_id INT PRIMARY KEY,
  material_name TEXT,
  material_type TEXT,
  material_description TEXT
);

CREATE TABLE resources (
  resource_id INT PRIMARY KEY,
  resource_name TEXT,
  resource_type TEXT,
  resource_link TEXT
);

CREATE TABLE courses (
  course_id INT PRIMARY KEY,
  course_name TEXT,
  course_description TEXT,
  course_material_id INT REFERENCES curriculum_materials(material_id)
);

CREATE TABLE course_material_mapping (
  course_material_id INT REFERENCES curriculum_materials(material_id),
  resource_id INT REFERENCES resources(resource_id)
);
