tables.table_schema:
CREATE TABLE academic_deadlines (
    id INT PRIMARY KEY,
    deadline_date DATE,
    event_name VARCHAR(255),
    description TEXT
);

CREATE TABLE academic_deadlines (
    deadline_id INT PRIMARY KEY,
    deadline_description TEXT,
    deadline_date TIMESTAMP,
    notification_sent BOOLEAN
);

CREATE TABLE notification_subscribers (
    subscriber_id INT PRIMARY KEY,
    subscriber_name VARCHAR(255),
    subscriber_email VARCHAR(255),
    subscription_date TIMESTAMP
);

CREATE TABLE notifications (
  id INT PRIMARY KEY,
  message TEXT,
  deadline_date DATE
);

CREATE TABLE users (
  user_id INT PRIMARY KEY,
  username TEXT,
  email TEXT
);

CREATE TABLE subscriptions (
  user_id INT,
  notification_id INT,
  FOREIGN KEY (user_id) REFERENCES users(user_id),
  FOREIGN KEY (notification_id) REFERENCES notifications(id)
);

CREATE TABLE Users (
    id INT PRIMARY KEY,
    username VARCHAR(50) UNIQUE,
    email VARCHAR(100),
    phone_number VARCHAR(20)
);

CREATE TABLE important_deadlines ( 
    deadline_id SERIAL PRIMARY KEY, 
    topic VARCHAR(100) NOT NULL, 
    deadline_date DATE NOT NULL 
);

CREATE TABLE students ( 
    student_id SERIAL PRIMARY KEY, 
    student_name VARCHAR(100) NOT NULL, 
    email VARCHAR(100) NOT NULL 
);

CREATE TABLE courses ( 
    course_id SERIAL PRIMARY KEY, 
    course_name VARCHAR(100) NOT NULL, 
    instructor VARCHAR(100) NOT NULL 
);

CREATE TABLE course_deadlines ( 
    deadline_id INT REFERENCES important_deadlines(deadline_id), 
    course_id INT REFERENCES courses(course_id), 
    PRIMARY KEY (deadline_id, course_id) 
);

CREATE TABLE student_courses ( 
    student_id INT REFERENCES students(student_id), 
    course_id INT REFERENCES courses(course_id), 
    PRIMARY KEY (student_id, course_id) 
);
