tables.table_schema:
CREATE TABLE IEPGoals (
  goal_id SERIAL PRIMARY KEY,
  student_id INT REFERENCES Students(student_id),
  goal_description TEXT NOT NULL,
  target_completion_date DATE
);

CREATE TABLE IEPProgress (
  progress_id SERIAL PRIMARY KEY,
  goal_id INT REFERENCES IEPGoals(goal_id),
  progress_description TEXT NOT NULL,
  date_recorded TIMESTAMP NOT NULL
);

CREATE TABLE Teacher (
    teacher_id SERIAL PRIMARY KEY,
    first_name VARCHAR(50) NOT NULL,
    last_name VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE
);

CREATE TABLE School (
    school_id SERIAL PRIMARY KEY,
    school_name VARCHAR(100) NOT NULL,
    address VARCHAR(200) NOT NULL,
    phone_number VARCHAR(15)
);

CREATE TABLE iep_progress (
    progress_id INT PRIMARY KEY,
    plan_id INT REFERENCES iep_plans(plan_id),
    progress_date DATE,
    progress_report TEXT
);

CREATE TABLE teachers (
    teacher_id INT PRIMARY KEY,
    first_name VARCHAR(50),
    last_name VARCHAR(50),
    department VARCHAR(50)
);

CREATE TABLE student_teacher_mapping (
    student_id INT REFERENCES students(student_id),
    teacher_id INT REFERENCES teachers(teacher_id),
    PRIMARY KEY (student_id, teacher_id)
);
