tables.table_schema:
CREATE TABLE StudentBehaviorData (
    student_id INT PRIMARY KEY,
    behavior_type VARCHAR(50),
    behavior_description TEXT,
    behavior_timestamp TIMESTAMP
);

CREATE TABLE Patterns (
    pattern_id INT PRIMARY KEY,
    pattern_name VARCHAR(50),
    pattern_description TEXT
);

CREATE TABLE students_data (
    student_id INT,
    student_name VARCHAR,
    behavior_type VARCHAR,
    behavior_date DATE
);

CREATE TABLE behaviors (
    behavior_id INT,
    behavior_description TEXT,
    behavior_category VARCHAR
);

CREATE TABLE interventions (
    intervention_id INT,
    intervention_description TEXT,
    intervention_date DATE
);

CREATE TABLE student_interventions (
    student_id INT,
    intervention_id INT,
    intervention_notes TEXT
);

CREATE TABLE ProgressReports (
  report_id SERIAL PRIMARY KEY,
  student_id INT REFERENCES StudentDetails(student_id),
  report_date DATE,
  progress_description TEXT 
);
