tables.table_schema:
CREATE TABLE students (
  student_id INT PRIMARY KEY,
  student_name VARCHAR(50) NOT NULL,
  disability_type VARCHAR(50) NOT NULL
);

CREATE TABLE assistive_technology_tools (
  tool_id INT PRIMARY KEY,
  tool_name VARCHAR(50) NOT NULL,
  tool_category VARCHAR(50) NOT NULL,
  tool_description TEXT
);

CREATE TABLE ToolUsageRecords(
  id SERIAL PRIMARY KEY,
  student_id INT NOT NULL,
  tool_name VARCHAR(50) NOT NULL,
  usage_date DATE NOT NULL
);

CREATE TABLE Students(
  id SERIAL PRIMARY KEY,
  student_name VARCHAR(100) NOT NULL,
  disability_type VARCHAR(50) NOT NULL
);

CREATE TABLE AssistiveTools(
  id SERIAL PRIMARY KEY,
  tool_name VARCHAR(50) NOT NULL,
  tool_description TEXT
);

CREATE TABLE UsageStatistics(
  id SERIAL PRIMARY KEY,
  tool_id INT NOT NULL,
  usage_duration INTERVAL NOT NULL,
  usage_frequency INT NOT NULL
);

CREATE TABLE AssistiveToolsUsage (
  student_id INT,
  tool_name VARCHAR(50),
  usage_date DATE,
  duration_minutes INT
);

CREATE TABLE Students (
  student_id INT PRIMARY KEY,
  student_name VARCHAR(50),
