tables.table_schema:
CREATE TABLE Accommodations (
    id UUID PRIMARY KEY,
    student_id UUID,
    accommodation_type VARCHAR(50),
    notes TEXT
);

CREATE TABLE Modifications (
    id UUID PRIMARY KEY,
    student_id UUID,
    modification_type VARCHAR(50),
    description TEXT
);

CREATE TABLE Students (
    id UUID PRIMARY KEY,
    name VARCHAR(50),
    grade INT,
    dob DATE
);

CREATE TABLE documentation_accommodations (
  accommodation_id INT PRIMARY KEY,
  student_id INT NOT NULL,
  accommodation_description VARCHAR(255) NOT NULL,
  date_created DATE NOT NULL
);

CREATE TABLE student_information (
  student_id INT PRIMARY KEY,
  student_name VARCHAR(100) NOT NULL,
  student_grade VARCHAR(10) NOT NULL,
  student_gender CHAR(1) NOT NULL
);

CREATE TABLE accommodation_history (
  history_id INT PRIMARY KEY,
  accommodation_id INT NOT NULL,
  modification_id INT NOT NULL,
  date_effective DATE NOT NULL
);

CREATE TABLE accommodations (
    accommodation_id INT PRIMARY KEY,
    student_id INT NOT NULL,
    accommodation_description TEXT,
    accommodation_date DATE
);

CREATE TABLE modifications (
    modification_id INT PRIMARY KEY,
    student_id INT NOT NULL,
    modification_description TEXT,
    modification_date DATE
);

CREATE TABLE students (
    student_id INT PRIMARY KEY,
    student_name VARCHAR(50) NOT NULL,
    student_grade INT,
    student_gender VARCHAR(10)
);

CREATE TABLE teachers (
    teacher_id INT PRIMARY KEY,
    teacher_name VARCHAR(50) NOT NULL,
    teacher_department VARCHAR(50)
);
