tables.table_schema:
CREATE TABLE professional_development_records (
  staff_id INT PRIMARY KEY,
  training_session VARCHAR(50),
  training_date DATE,
  certification_status BOOLEAN
);

CREATE TABLE staff_training (
   staff_id INT PRIMARY KEY,
   staff_name VARCHAR(50) NOT NULL,
   training_date DATE NOT NULL,
   training_subject VARCHAR(100) NOT NULL
);

CREATE TABLE certification_tracking (
   certification_id INT PRIMARY KEY,
   certification_name VARCHAR(50) NOT NULL,
   certification_date DATE NOT NULL,
   staff_id INT NOT NULL
);

CREATE TABLE training_resources (
   resource_id INT PRIMARY KEY,
   resource_name VARCHAR(50) NOT NULL,
   resource_type VARCHAR(20) NOT NULL,
   resource_link VARCHAR(100) NOT NULL
);

CREATE TABLE ProfessionalDevelopmentRecords ( 
    staff_id INTEGER PRIMARY KEY, 
    training_type TEXT, 
    training_date DATE, 
    certification_status BOOLEAN 
);

CREATE TABLE StaffInformation ( 
    staff_id INTEGER PRIMARY KEY, 
    staff_name TEXT, 
    position TEXT, 
    department TEXT 
);

CREATE TABLE TrainingCourses ( 
    course_id INTEGER PRIMARY KEY, 
    course_name TEXT, 
    course_description TEXT, 
    course_duration INTEGER 
);

CREATE TABLE CertificationStatus ( 
    staff_id INTEGER PRIMARY KEY, 
    course_id INTEGER, 
    completion_date DATE, 
    certification_expiry_date DATE 
);

CREATE TABLE special_education_staff (
    staff_id INT PRIMARY KEY,
    staff_name VARCHAR(255) NOT NULL,
    certification_status VARCHAR(50) NOT NULL
);

CREATE TABLE training_records (
    record_id INT PRIMARY KEY,
    staff_id INT REFERENCES special_education_staff(staff_id),
    training_date DATE NOT NULL,
    training_topic VARCHAR(100) NOT NULL
);

CREATE TABLE certification_logs (
    log_id INT PRIMARY KEY,
    staff_id INT REFERENCES special_education_staff(staff_id),
    certification_date DATE NOT NULL,
    certification_type VARCHAR(50) NOT NULL
);

CREATE TABLE training_materials (
    material_id INT PRIMARY KEY,
    material_name VARCHAR(100) NOT NULL,
    material_url VARCHAR(255) NOT NULL
);

CREATE TABLE training_assignments (
    assignment_id INT PRIMARY KEY,
    staff_id INT REFERENCES special_education_staff(staff_id),
    material_id INT REFERENCES training_materials(material_id),
    assigned_date DATE NOT NULL
);
