tables.table_schema:
CREATE TABLE therapists (
  id INT PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  specialization VARCHAR(50) NOT NULL,
  feedback TEXT
);

CREATE TABLE therapists_feedback (
  therapist_id INT PRIMARY KEY,
  student_id INT,
  feedback_text TEXT,
  feedback_date TIMESTAMP
);

CREATE TABLE specialists_feedback (
  specialist_id INT PRIMARY KEY,
  student_id INT,
  feedback_text TEXT,
  feedback_date TIMESTAMP
);

CREATE TABLE caregivers_feedback (
  caregiver_id INT PRIMARY KEY,
  student_id INT,
  feedback_text TEXT,
  feedback_date TIMESTAMP
);

CREATE TABLE therapists (
    therapist_id INT PRIMARY KEY,
    therapist_name VARCHAR(255),
    therapy_type VARCHAR(50),
    feedback TEXT
);

CREATE TABLE specialists (
    specialist_id INT PRIMARY KEY,
    specialist_name VARCHAR(255),
    specialty_area VARCHAR(50),
    feedback TEXT
);

CREATE TABLE caregivers (
    caregiver_id INT PRIMARY KEY,
    caregiver_name VARCHAR(255),
    relationship VARCHAR(50),
    feedback TEXT
);

CREATE TABLE students (
    student_id INT PRIMARY KEY,
    student_name VARCHAR(255),
    disability_type VARCHAR(50),
    feedback TEXT
);

CREATE TABLE TherapistsFeedback (
   id SERIAL PRIMARY KEY,
   therapist_name VARCHAR(100),
   feedback_text TEXT,
   created_at TIMESTAMP
);

CREATE TABLE SpecialistsFeedback (
   id SERIAL PRIMARY KEY,
   specialist_name VARCHAR(100),
   feedback_text TEXT,
   created_at TIMESTAMP
);

CREATE TABLE Disabilities (
   id SERIAL PRIMARY KEY,
   disability_type VARCHAR(50), 
   description TEXT,
   support_needed TEXT
);
