tables.table_schema:
CREATE TABLE students (
  student_id INT PRIMARY KEY,
  student_name VARCHAR(50) NOT NULL,
  student_email VARCHAR(50) UNIQUE NOT NULL,
  student_interests VARCHAR(255)
);

CREATE TABLE mentors (
  mentor_id INT PRIMARY KEY,
  mentor_name VARCHAR(50) NOT NULL,
  mentor_email VARCHAR(50) UNIQUE NOT NULL,
  mentor_expertise VARCHAR(255)
);

CREATE TABLE students (
    student_id INT PRIMARY KEY,
    student_name VARCHAR(50),
    student_email VARCHAR(50)
);

CREATE TABLE tutors (
    tutor_id INT PRIMARY KEY,
    tutor_name VARCHAR(50),
    tutor_email VARCHAR(50)
);

CREATE TABLE subjects (
    subject_id INT PRIMARY KEY,
    subject_name VARCHAR(50)
);

CREATE TABLE student_interests (
    student_id INT,
    subject_id INT,
    FOREIGN KEY (student_id) REFERENCES students(student_id),
    FOREIGN KEY (subject_id) REFERENCES subjects(subject_id)
);

CREATE TABLE tutor_subjects (
    tutor_id INT,
    subject_id INT,
    FOREIGN KEY (tutor_id) REFERENCES tutors(tutor_id),
    FOREIGN KEY (subject_id) REFERENCES subjects(subject_id)
);
