tables.table_schema:
CREATE TABLE student_language_proficiency (
  student_id UUID PRIMARY KEY,
  country_code VARCHAR(3),
  language VARCHAR(50),
  proficiency_level VARCHAR(10)
);

CREATE TABLE language_proficiency_levels (
    student_id INT,
    language_code VARCHAR(3),
    proficiency_level INT,
    country_code VARCHAR(2)
);

CREATE TABLE country_data (
    country_code VARCHAR(2),
    country_name VARCHAR(50),
    continent VARCHAR(50)
);

CREATE TABLE language_proficiency (
    student_id UUID PRIMARY KEY,
    country VARCHAR(255),
    language VARCHAR(255),
    proficiency_level INT
);

CREATE TABLE program_customization (
    student_id UUID PRIMARY KEY,
    learning_program VARCHAR(255),
    program_status VARCHAR(255),
    program_date DATE
);

CREATE TABLE Countries (
  country_id INT PRIMARY KEY,
  country_name VARCHAR(50) NOT NULL
);

CREATE TABLE Languages (
  language_id INT PRIMARY KEY,
  language_name VARCHAR(50) NOT NULL
);

CREATE TABLE ProficiencyLevels (
  level_id INT PRIMARY KEY,
  level_name VARCHAR(50) NOT NULL,
  description TEXT
);
