tables.table_schema:
CREATE TABLE curriculum_courses (
    course_id INT PRIMARY KEY,
    course_name VARCHAR(255) NOT NULL,
    course_description TEXT,
    course_credit_hours INT
);

CREATE TABLE curriculum_data (
  program_id INT PRIMARY KEY,
  program_name VARCHAR(255) NOT NULL,
  campus_name VARCHAR(255) NOT NULL,
  program_type ENUM('Undergraduate', 'Graduate', 'Doctoral') NOT NULL
);

CREATE TABLE campus (
    campus_id INT PRIMARY KEY,
    campus_name VARCHAR(50) NOT NULL,
    campus_location VARCHAR(100),
    campus_capacity INT
);

CREATE TABLE course (
    course_id INT PRIMARY KEY,
    course_name VARCHAR(50) NOT NULL,
    course_description TEXT,
    course_duration INT
);

CREATE TABLE curriculum_programs (
    program_id UUID PRIMARY KEY,
    program_name STRING,
    campus_location STRING,
    program_description STRING
);

CREATE TABLE courses (
    course_id UUID PRIMARY KEY,
    course_name STRING,
    program_id UUID REFERENCES curriculum_programs(program_id),
    course_description STRING
);

CREATE TABLE teachers (
    teacher_id UUID PRIMARY KEY,
    teacher_name STRING,
    teacher_email STRING,
    campus_location STRING
);

CREATE TABLE students (
    student_id UUID PRIMARY KEY,
    student_name STRING,
    student_email STRING,
    program_id UUID REFERENCES curriculum_programs(program_id)
);

CREATE TABLE enrollments (
    enrollment_id UUID PRIMARY KEY,
    student_id UUID REFERENCES students(student_id),
    course_id UUID REFERENCES courses(course_id),
    enrollment_date TIMESTAMP
);
