tables.table_schema:
CREATE TABLE curriculum ( 
curriculum_id UUID PRIMARY KEY, 
curriculum_name VARCHAR(100) NOT NULL, 
age_range VARCHAR(50) NOT NULL 
);

CREATE TABLE curriculum (
  curriculum_id INT PRIMARY KEY,
  curriculum_name VARCHAR(255) NOT NULL,
  age_group VARCHAR(50) NOT NULL
);

CREATE TABLE lesson_plan (
  lesson_id INT PRIMARY KEY,
  lesson_name VARCHAR(255) NOT NULL,
  curriculum_id INT REFERENCES curriculum(curriculum_id),
  lesson_duration INT NOT NULL
);

CREATE TABLE learning_objective (
  objective_id INT PRIMARY KEY,
  objective_description TEXT NOT NULL,
  lesson_id INT REFERENCES lesson_plan(lesson_id)
);

CREATE TABLE assessment (
  assessment_id INT PRIMARY KEY,
  assessment_type VARCHAR(50) NOT NULL,
  objective_id INT REFERENCES learning_objective(objective_id),
  assessment_date DATE
);

CREATE TABLE curriculum (
    id SERIAL PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    description TEXT
);

CREATE TABLE lesson_plans (
    id SERIAL PRIMARY KEY,
    title VARCHAR(100) NOT NULL,
    topics TEXT[],
    duration INTEGER
);

CREATE TABLE activities (
    id SERIAL PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    description TEXT,
    type VARCHAR(50)
);

CREATE TABLE resources (
    id SERIAL PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    type VARCHAR(50),
    link VARCHAR(200)
);

CREATE TABLE assessments (
    id SERIAL PRIMARY KEY,
    title VARCHAR(100),
    description TEXT,
    due_date DATE
);
