tables.table_schema:
CREATE TABLE certification_records (
  certification_id INT PRIMARY KEY,
  teacher_id INT,
  certification_name VARCHAR(50),
  certification_date DATE
);
CREATE TABLE training_records (
  training_id INT PRIMARY KEY,
  staff_id INT,
  training_topic VARCHAR(50),
  training_date DATE
);

CREATE TABLE certification_records (
    certification_id SERIAL PRIMARY KEY,
    certification_type VARCHAR(50),
    issue_date DATE,
    expiration_date DATE
);

CREATE TABLE training_records (
    training_id SERIAL PRIMARY KEY,
    training_name VARCHAR(100),
    training_provider VARCHAR(100),
    completion_date DATE
);

CREATE TABLE staff_records (
    staff_id SERIAL PRIMARY KEY,
    staff_name VARCHAR(100),
    staff_role VARCHAR(50),
    hire_date DATE
);

CREATE TABLE certifications (
  certification_id INT PRIMARY KEY,
  certification_name VARCHAR(100),
  certification_org VARCHAR(100),
  certification_duration INT
);
