tables.table_schema:
CREATE TABLE early_childhood_education_resources (
  resource_id UUID PRIMARY KEY,
  resource_name VARCHAR(255),
  resource_type VARCHAR(50),
  resource_description TEXT
);

CREATE TABLE teacher_materials_available (
  material_id UUID PRIMARY KEY,
  material_name VARCHAR(255),
  material_type VARCHAR(50),
  material_quantity INT
);

CREATE TABLE teachers (
  id INT PRIMARY KEY,
  name VARCHAR(50),
  email VARCHAR(100)
);

CREATE TABLE resources (
  resource_id INT PRIMARY KEY,
  resource_name VARCHAR(100),
  description TEXT,
  availability BOOLEAN
);

CREATE TABLE materials (
  material_id INT PRIMARY KEY,
  material_name VARCHAR(100),
  quantity INT,
  in_stock BOOLEAN
);

CREATE TABLE early_childhood_education_resources (
  resource_id INT PRIMARY KEY,
  resource_name VARCHAR(255),
  resource_description TEXT,
  resource_type VARCHAR(50)
);

CREATE TABLE teacher_materials (
  material_id INT PRIMARY KEY,
  material_name VARCHAR(255),
  material_type VARCHAR(50),
  quantity INT
);

CREATE TABLE resource_categories (
  category_id INT PRIMARY KEY,
  category_name VARCHAR(100),
  category_description TEXT
);

CREATE TABLE teacher_resource_relationship (
  teacher_id INT,
  resource_id INT,
  FOREIGN KEY (teacher_id) REFERENCES teachers(teacher_id),
  FOREIGN KEY (resource_id) REFERENCES early_childhood_education_resources(resource_id)
);
