tables.table_schema:
CREATE TABLE student_grades (
  student_id INT PRIMARY KEY,
  student_name TEXT,
  grade FLOAT
);

CREATE TABLE academic_performance (
  student_id INT REFERENCES student_grades(student_id),
  exam_type TEXT,
  score INT
);

CREATE TABLE Students (
    student_id SERIAL PRIMARY KEY,
    student_name VARCHAR(100) NOT NULL,
    grade INT,
    academic_performance VARCHAR(50)
);

CREATE TABLE Courses (
    course_id SERIAL PRIMARY KEY,
    course_name VARCHAR(100) NOT NULL,
    course_credit INT,
    course_instructor VARCHAR(100)
);

CREATE TABLE students (
  id SERIAL PRIMARY KEY,
  first_name VARCHAR(50) NOT NULL,
  last_name VARCHAR(50) NOT NULL,
  grade DECIMAL(5,2) NOT NULL
);

CREATE TABLE courses (
  course_id SERIAL PRIMARY KEY,
  course_name VARCHAR(100) NOT NULL,
  credit_hours INT NOT NULL
);

CREATE TABLE student_courses (
  student_id INT REFERENCES students(id) NOT NULL,
  course_id INT REFERENCES courses(course_id) NOT NULL,
  grade DECIMAL(5,2) NOT NULL
);

CREATE TABLE academic_performance (
  student_id INT REFERENCES students(id) NOT NULL,
  gpa DECIMAL(3,2) NOT NULL,
  enrollment_year INT NOT NULL
);

CREATE TABLE students (
    student_id INT PRIMARY KEY,
    student_name VARCHAR(255),
    grade INT
);

CREATE TABLE courses (
    course_id INT PRIMARY KEY,
    course_name VARCHAR(255),
    credits INT
);

CREATE TABLE enrollments (
    enrollment_id INT PRIMARY KEY,
    student_id INT,
    course_id INT,
    grade INT
);

CREATE TABLE professors (
    professor_id INT PRIMARY KEY,
    professor_name VARCHAR(255),
    department VARCHAR(255)
);

CREATE TABLE departments (
    department_id INT PRIMARY KEY,
    department_name VARCHAR(255)
);
