tables.table_schema:
CREATE TABLE Faculty (
    faculty_id INT,
    faculty_name VARCHAR(50),
    teaching_schedule TEXT,
    research_interests TEXT
);

CREATE TABLE faculty_info (
    faculty_id INT PRIMARY KEY,
    first_name VARCHAR(50) NOT NULL,
    last_name VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE,
    department VARCHAR(50)
);

CREATE TABLE teaching_schedule (
    schedule_id INT PRIMARY KEY,
    faculty_id INT REFERENCES faculty_info(faculty_id),
    course_id INT,
    day_of_week VARCHAR(10),
    start_time TIME,
    end_time TIME
);

CREATE TABLE research_interests (
    research_id INT PRIMARY KEY,
    faculty_id INT REFERENCES faculty_info(faculty_id),
    research_area VARCHAR(100)
);

CREATE TABLE departments (
    department_id INT PRIMARY KEY,
    department_name VARCHAR(50)
);

CREATE TABLE Faculty (
    faculty_id INT PRIMARY KEY,
    faculty_name VARCHAR(50) NOT NULL,
    teaching_schedule VARCHAR(100),
    research_interests TEXT
);

CREATE TABLE ResearchInterests (
    interest_id INT PRIMARY KEY,
    faculty_id INT,
    research_area VARCHAR(50),
    FOREIGN KEY (faculty_id) REFERENCES Faculty(faculty_id)
);

CREATE TABLE Courses (
    course_code VARCHAR(20) PRIMARY KEY,
    course_name VARCHAR(50) NOT NULL,
    course_description TEXT
);
