tables.table_schema:
CREATE TABLE authors (
    author_id SERIAL PRIMARY KEY,
    author_name VARCHAR(255)
);

CREATE TABLE locations (
    location_id SERIAL PRIMARY KEY,
    shelf_num INT,
    aisle_num INT,
    level_num INT
);

CREATE TABLE circulation_records (
    record_id SERIAL PRIMARY KEY,
    book_id INT,
    checkout_date DATE,
    return_date DATE
);
