tables.table_schema:
CREATE TABLE solar_panels (
	solar_panel_id UUID PRIMARY KEY,
	solar_panel_name VARCHAR(255),
	solar_panel_capacity FLOAT,
	solar_panel_location VARCHAR(255)
);

CREATE TABLE wind_turbines (
	wind_turbine_id UUID PRIMARY KEY,
	wind_turbine_name VARCHAR(255),
	wind_turbine_capacity FLOAT,
	wind_turbine_location VARCHAR(255)
);

CREATE TABLE solar_panels (
    id INT PRIMARY KEY,
    location VARCHAR(255),
    efficiency DECIMAL(5,2),
    installation_date DATE
);

CREATE TABLE solar_panels (
    panel_id INT PRIMARY KEY,
    panel_location TEXT,
    panel_capacity DECIMAL
);

CREATE TABLE wind_turbines (
    turbine_id INT PRIMARY KEY,
    turbine_location TEXT,
    turbine_capacity DECIMAL
);

CREATE TABLE energy_consumption (
    consumption_id INT PRIMARY KEY,
    consumption_date DATE,
    energy_consumed DECIMAL,
    PRIMARY KEY (consumption_id)
);
