tables.table_schema:
CREATE TABLE transportation_infrastructure (
    city_name VARCHAR(50) NOT NULL,
    transportation_mode VARCHAR(20) NOT NULL,
    efficiency_rating FLOAT NOT NULL
);

CREATE TABLE transportation_agencies (
    agency_id INT PRIMARY KEY,
    agency_name VARCHAR(255) NOT NULL,
    city VARCHAR(100) NOT NULL,
    state VARCHAR(50) NOT NULL
);

CREATE TABLE UrbanAreas (
  area_id INT PRIMARY KEY,
  area_name TEXT
);

CREATE TABLE Roads (
  road_id INT PRIMARY KEY,
  road_name TEXT,
  length_km FLOAT
);

CREATE TABLE BusStops (
  stop_id INT PRIMARY KEY,
  stop_name TEXT,
  location_lat FLOAT,
  location_long FLOAT
);

CREATE TABLE TrafficCongestion (
  congestion_id INT PRIMARY KEY,
  road_id INT,
  congestion_level INT,
  report_date DATE
);
