tables.table_schema:
CREATE TABLE CrimeReports (
    report_id SERIAL PRIMARY KEY,
    report_date DATE NOT NULL,
    crime_type VARCHAR(50) NOT NULL,
    neighborhood VARCHAR(50) NOT NULL
);

CREATE TABLE Neighborhoods (
    neighborhood_id SERIAL PRIMARY KEY,
    neighborhood_name VARCHAR(50) NOT NULL,
    city VARCHAR(50) NOT NULL
);

CREATE TABLE CrimeStatistics (
    statistic_id SERIAL PRIMARY KEY,
    report_id INT REFERENCES CrimeReports(report_id),
    neighborhood_id INT REFERENCES Neighborhoods(neighborhood_id),
    crime_count INT NOT NULL
);

CREATE TABLE Neighborhoods (
    id INT PRIMARY KEY,
    neighborhood_name VARCHAR(255) NOT NULL,
    city VARCHAR(255) NOT NULL
);

