tables.table_schema:
CREATE TABLE smart_meters (
    meter_id INT PRIMARY KEY,
    location VARCHAR,
    energy_usage DECIMAL
);

CREATE TABLE smart_meters (
    meter_id INT PRIMARY KEY,
    location TEXT,
    customer_id INT,
    installation_date DATE
);

CREATE TABLE energy_consumption (
    consumption_id INT PRIMARY KEY,
    meter_id INT,
    consumption_amount FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE customer_info (
    customer_id INT PRIMARY KEY,
    customer_name TEXT,
    address TEXT,
    email TEXT
);

CREATE TABLE meter_readings (
    reading_id INT PRIMARY KEY,
    meter_id INT,
    reading_value FLOAT,
    reading_date DATE
);

CREATE TABLE smart_meters (
  id INT PRIMARY KEY,
  meter_id INT UNIQUE,
  location VARCHAR(255),
  energy_consumption DECIMAL
);

CREATE TABLE device_info (
  id INT PRIMARY KEY,
  device_id INT UNIQUE,
  device_type VARCHAR(50),
  installation_date DATE
);
