tables.table_schema:
CREATE TABLE smart_home_devices (
  device_id INT PRIMARY KEY,
  device_name VARCHAR(255),
  device_type VARCHAR(50),
  device_location VARCHAR(100)
);

CREATE TABLE SmartHomeDevices (
 device_id UUID PRIMARY KEY,
 device_name STRING,
 device_type STRING,
 device_location STRING
);

CREATE TABLE DeviceSettings (
 setting_id UUID PRIMARY KEY,
 device_id UUID,
 setting_name STRING,
 setting_value STRING
);

CREATE TABLE DeviceConfigurations (
 config_id UUID PRIMARY KEY,
 device_id UUID,
 config_name STRING,
 config_value STRING
);

CREATE TABLE DeviceUsers (
 user_id UUID PRIMARY KEY,
 device_id UUID,
 user_name STRING,
 user_role STRING
);

CREATE TABLE RoomConfigurations (
    room_id INT PRIMARY KEY,
    room_name VARCHAR(100),
    room_type VARCHAR(50)
);

CREATE TABLE UserSettings (
    user_id INT PRIMARY KEY,
    user_name VARCHAR(255),
    user_email VARCHAR(100),
    notification_preference BOOLEAN
);
