tables.table_schema:
CREATE TABLE iot_cameras (
  camera_id INT PRIMARY KEY,
  camera_name VARCHAR(50) NOT NULL,
  location VARCHAR(100) NOT NULL
);

CREATE TABLE facial_data (
  data_id INT PRIMARY KEY,
  camera_id INT NOT NULL,
  face_encoding BYTEA,
  detection_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE facial_recognition_data (
    id SERIAL PRIMARY KEY,
    camera_id INT NOT NULL,
    image_data BYTEA NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE camera_details (
    camera_id SERIAL PRIMARY KEY,
    location VARCHAR(50) NOT NULL,
    owner_name VARCHAR(50) NOT NULL
);

CREATE TABLE user_permissions (
    user_id SERIAL PRIMARY KEY,
    camera_id INT NOT NULL,
    permission_level INT NOT NULL
);

CREATE TABLE Camera (
  camera_id UUID PRIMARY KEY,
  location VARCHAR(255),
  resolution VARCHAR(10),
  brand VARCHAR(50),
);

CREATE TABLE Person (
  person_id UUID PRIMARY KEY,
  name VARCHAR(100),
  age INT,
  gender VARCHAR(10),
);

CREATE TABLE Recognition (
  recognition_id UUID PRIMARY KEY,
  camera_id UUID REFERENCES Camera(camera_id),
  person_id UUID REFERENCES Person(person_id),
  timestamp TIMESTAMP,
);

CREATE TABLE Log (
  log_id UUID PRIMARY KEY,
  action VARCHAR(50),
  timestamp TIMESTAMP,
  details VARCHAR(255),
);
