tables.table_schema:
CREATE TABLE solar_farms (
  id INT PRIMARY KEY,
  location GEOGRAPHY(POINT),
  capacity FLOAT,
  average_sunlight_hours FLOAT
);

CREATE TABLE weather_data (
  date DATE,
  temperature FLOAT,
  humidity FLOAT,
 wind_speed FLOAT
);

CREATE TABLE site_selection (
  site_id INT PRIMARY KEY,
  site_location GEOGRAPHY(POLYGON),
  suitability_score FLOAT
);

CREATE TABLE SolarFarms (
    id INT PRIMARY KEY,
    location GEOGRAPHY(POINT) NOT NULL,
    capacity FLOAT NOT NULL,
    max_solar_intensity FLOAT NOT NULL
);

CREATE TABLE WeatherData (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP NOT NULL,
    temperature FLOAT NOT NULL,
    humidity FLOAT NOT NULL
);

CREATE TABLE SiteEvaluation (
    id INT PRIMARY KEY,
    site_id INT NOT NULL,
    evaluation_date DATE NOT NULL,
    suitability_score FLOAT NOT NULL
);

CREATE TABLE PowerOutput (
    id INT PRIMARY KEY,
    site_id INT NOT NULL,
    output_date DATE NOT NULL,
    power_generated FLOAT NOT NULL
);

CREATE TABLE sites (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    location GEOMETRY,
    capacity FLOAT
);
