tables.table_schema:
CREATE TABLE battery_storage (
    id INT PRIMARY KEY,
    location VARCHAR(255),
    capacity FLOAT,
    voltage FLOAT
);

CREATE TABLE performance_metrics (
    id INT PRIMARY KEY,
    battery_id INT,
    timestamp TIMESTAMP,
    performance_rating INT
);

CREATE TABLE battery_storage (
    battery_id UUID PRIMARY KEY,
    location VARCHAR(255),
    capacity DECIMAL,
    efficiency FLOAT
);

CREATE TABLE energy_storage (
    storage_id UUID PRIMARY KEY,
    capacity DECIMAL,
    type VARCHAR(255)
);

CREATE TABLE energy_production (
    production_id UUID,
    date DATE,
    production_value DECIMAL,
    PRIMARY KEY (production_id, date)
);

CREATE TABLE energy_consumption (
    consumption_id UUID PRIMARY KEY,
    date DATE,
    consumption_value DECIMAL
);
