tables.table_schema:
CREATE TABLE customer_shipping_preferences (
    customer_id INT,
    shipping_address VARCHAR,
    shipping_method VARCHAR,
    preference_date DATE
);

CREATE TABLE transportation_sector (
    sector_id INT,
    sector_name VARCHAR,
    transportation_mode VARCHAR
);

CREATE TABLE CustomerPreferences (
  customer_id INT PRIMARY KEY,
  shipping_method VARCHAR(50),
  preferred_delivery_time TIME,
  shipping_cost DECIMAL(10, 2)
);

CREATE TABLE customer_preferences (
    customer_id INT,
    shipping_option VARCHAR(50),
    preferred_carrier VARCHAR(50),
    preferred_delivery_time TIMESTAMP
);

CREATE TABLE shipping_options (
    option_id INT,
    option_name VARCHAR(50),
    option_description TEXT
);

CREATE TABLE carriers (
    carrier_id INT,
    carrier_name VARCHAR(50),
    carrier_type VARCHAR(50)
);

CREATE TABLE delivery_times (
    time_id INT,
    time_description VARCHAR(100)
);

CREATE TABLE customers (
  customer_id INT PRIMARY KEY,
  name VARCHAR(50),
  location VARCHAR(100),
  contact_number VARCHAR(15)
);

CREATE TABLE shipping_companies (
  company_id INT PRIMARY KEY,
  company_name VARCHAR(50),
  location VARCHAR(100),
  contact_number VARCHAR(15)
);

CREATE TABLE products (
  product_id INT PRIMARY KEY,
  product_name VARCHAR(50),
  weight FLOAT,
  description TEXT
);

CREATE TABLE shipping_rates (
  rate_id INT PRIMARY KEY,
  origin_location VARCHAR(100),
  destination_location VARCHAR(100),
  cost_per_kg FLOAT
);
