tables.table_schema:
CREATE TABLE fuel_emission_data (
    vehicle_id INT,
    fuel_type VARCHAR(50),
    fuel_consumption DECIMAL(10, 2),
    emissions_rating INT
);

CREATE TABLE fuel_consumption (
    vehicle_id INT,
    fuel_type VARCHAR(50),
    fuel_quantity DECIMAL(10, 2),
    consumption_date DATE
);

CREATE TABLE vehicle_emissions (
    vehicle_id INT,
    co_emissions DECIMAL(10, 2),
    co2_emissions DECIMAL(10, 2),
    emission_date DATE
);

CREATE TABLE FuelConsumption (
    vehicle_id INT PRIMARY KEY,
    fuel_type VARCHAR(50),
    liters_consumed DECIMAL,
    consumption_date DATE
);

CREATE TABLE VehicleEmissions (
    vehicle_id INT PRIMARY KEY,
    emission_type VARCHAR(50),
    emission_value DECIMAL,
    measurement_date DATE
);

CREATE TABLE vehicle_emissions (
    id SERIAL PRIMARY KEY,
    vehicle_id INT,
    emission_type VARCHAR(50),
    emission_value DECIMAL
);

CREATE TABLE vehicle_data (
    id SERIAL PRIMARY KEY,
    vehicle_id INT,
    model VARCHAR(50),
    year INT
);

CREATE TABLE fuel_types (
    id SERIAL PRIMARY KEY,
    fuel_type VARCHAR(50),
    description TEXT
);

CREATE TABLE fuel_consumption (
  vehicle_id INT PRIMARY KEY,
  fuel_type VARCHAR(50),
  fuel_consumed DECIMAL(10,2),
  fuel_cost DECIMAL(10,2)
);

CREATE TABLE vehicle_emissions (
  vehicle_id INT PRIMARY KEY,
  co2_emissions DECIMAL(10,2),
  nox_emissions DECIMAL(10,2),
  particulate_matter DECIMAL(10,2)
);

CREATE TABLE vehicle_info (
  vehicle_id INT PRIMARY KEY,
  make VARCHAR(50),
  model VARCHAR(50),
  year INT
);

CREATE TABLE driver_info (
  driver_id INT PRIMARY KEY,
  driver_name VARCHAR(50),
  driver_license VARCHAR(20),
  driver_age INT
);
