tables.table_schema:
CREATE TABLE driver_performance (
  driver_id INT PRIMARY KEY,
  total_trips INT,
  total_miles INT,
  total_accidents INT
);

CREATE TABLE safety_metrics (
  driver_id INT PRIMARY KEY,
  seatbelt_usage BOOLEAN,
  speed_limit_violations INT,
  alcohol_test BOOLEAN
);

CREATE TABLE drivers (
    driver_id SERIAL PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    employee_id INT UNIQUE,
    license_number VARCHAR(20) UNIQUE
);

CREATE TABLE drivers (
    driver_id INT PRIMARY KEY,
    driver_name VARCHAR(255),
    license_number VARCHAR(20),
    fleet_id INT
);

CREATE TABLE fleets (
    fleet_id INT PRIMARY KEY,
    fleet_name VARCHAR(100),
    fleet_manager VARCHAR(255),
    location VARCHAR(100)
);
