tables.table_schema:
CREATE TABLE SensorData ( 
  sensor_id INT PRIMARY KEY, 
  sensor_value FLOAT, 
  timestamp TIMESTAMP 
);

CREATE TABLE anomaly_data (
    sensor_id INT PRIMARY KEY,
    sensor_value FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE sensor_metadata (
    sensor_id INT PRIMARY KEY,
    location VARCHAR(50),
    manufacturer VARCHAR(50),
    calibration_date DATE
);

CREATE TABLE anomaly_detection (
    detection_id INT PRIMARY KEY,
    sensor_id INT,
    anomaly_category VARCHAR(50),
    detection_date DATE
);

CREATE TABLE outlier_data (
    outlier_id INT PRIMARY KEY,
    sensor_id INT,
    outlier_value FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE sensor_data(
    sensor_id INT PRIMARY KEY,
    sensor_name VARCHAR(50),
    sensor_value FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE quality_control(
    qc_id INT PRIMARY KEY,
    sensor_id INT,
    status VARCHAR(20),
    comments TEXT
);

CREATE TABLE sensor_metadata(
    sensor_id INT PRIMARY KEY,
    location VARCHAR(100),
    manufacturer VARCHAR(50),
    installation_date DATE
);
