tables.table_schema:
CREATE TABLE social_media_posts (
    post_id INT PRIMARY KEY,
    post_content TEXT,
    user_id INT,
    post_date TIMESTAMP
);

CREATE TABLE sentiment_analysis_results (
    analysis_id INT PRIMARY KEY,
    post_id INT,
    sentiment_score FLOAT,
    sentiment_category VARCHAR(100)
);

CREATE TABLE social_media_comments (
    comment_id INT PRIMARY KEY,
    user_id INT,
    comment_text VARCHAR(255),
    sentiment_score FLOAT
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    registration_date DATE
);

CREATE TABLE sentiment_analysis_results (
    result_id INT PRIMARY KEY,
    comment_id INT,
    sentiment_label VARCHAR(20),
    analysis_date TIMESTAMP
);

CREATE TABLE Sentiment_Data (
    id INT PRIMARY KEY,
    tweet TEXT,
    sentiment_score INT
);

CREATE TABLE User_Profile (
    user_id INT PRIMARY KEY,
    username TEXT,
    location TEXT,
    followers_count INT
);

CREATE TABLE User_Sentiment (
    id INT AUTO_INCREMENT PRIMARY KEY,
    user_id INT,
    sentiment_score INT,
    sentiment_analysis_date DATE
);

CREATE TABLE Trending_Hashtags (
    hashtag_id INT PRIMARY KEY,
    hashtag TEXT,
    tweet_count INT
);

CREATE TABLE sentiment_analysis_results (
    analysis_id INT PRIMARY KEY,
    post_id INT,
    analysis_result TEXT,
    accuracy FLOAT
);
