CREATE TABLE GreenBuildings (
    building_id UUID PRIMARY KEY,
    building_name VARCHAR(255) NOT NULL,
    address VARCHAR(255) NOT NULL,
    leed_certification_status VARCHAR(50) NOT NULL
);

CREATE TABLE green_buildings (
  building_id INT PRIMARY KEY,
  building_name VARCHAR(255) NOT NULL,
  certification_status VARCHAR(50) NOT NULL
);

CREATE TABLE Green_Buildings (
    building_id UUID PRIMARY KEY,
    building_name VARCHAR(255) NOT NULL,
    certification_status VARCHAR(50) NOT NULL
);

CREATE TABLE Certification (
    certification_id UUID PRIMARY KEY,
    certification_name VARCHAR(100) NOT NULL,
    certification_level VARCHAR(50)

);

CREATE TABLE Building_Certification (
    building_id UUID REFERENCES Green_Buildings(building_id),
    certification_id UUID REFERENCES Certification(certification_id),
    certification_date DATE,
    PRIMARY KEY (building_id, certification_id)
);

CREATE TABLE LEEDCategories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(50) NOT NULL,
    description TEXT
);

CREATE TABLE Users (
    user_id INT PRIMARY KEY,
    username VARCHAR(30) UNIQUE NOT NULL,
    password VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE NOT NULL
);

CREATE TABLE UserRoles (
    user_id INT NOT NULL,
    role_name VARCHAR(20) NOT NULL,
    FOREIGN KEY (user_id) REFERENCES Users(user_id)
);
