tables.table_schema:
CREATE TABLE water_usage_data (
    facility_id INT,
    water_meter_id INT,
    usage_date DATE,
    usage_amount DECIMAL(10,2)
);

CREATE TABLE water_usage_data (
    id INT PRIMARY KEY,
    facility_id INT,
    water_usage DECIMAL
);

CREATE TABLE facilities (
    facility_id INT PRIMARY KEY,
    facility_name TEXT,
    location TEXT
);

CREATE TABLE WaterConsumption (
    building_id INT,
    date DATE,
    consumption FLOAT
);

CREATE TABLE Buildings (
    building_id INT PRIMARY KEY,
    building_name VARCHAR(255),
    address VARCHAR(255)
);

CREATE TABLE Facilities (
    facility_id INT PRIMARY KEY,
    facility_name VARCHAR(255),
    facility_type VARCHAR(50)
);

CREATE TABLE facility (
    facility_id UUID PRIMARY KEY,
    facility_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL
);

CREATE TABLE water_usage (
    usage_id UUID PRIMARY KEY,
    meter_id UUID REFERENCES water_meter(meter_id),
    usage_amount DECIMAL(10,2) NOT NULL,
    usage_date DATE NOT NULL
);

CREATE TABLE building (
    building_id UUID PRIMARY KEY,
    facility_id UUID REFERENCES facility(facility_id),
    building_name VARCHAR(255) NOT NULL,
    building_type VARCHAR(255) NOT NULL
);

CREATE TABLE green_building_facility (
    id INT PRIMARY KEY,
    facility_name VARCHAR(255),
    location VARCHAR(255)
);

CREATE TABLE water_usage_data (
    data_id INT PRIMARY KEY,
    facility_id INT,
    usage_date DATE,
    total_water_used DECIMAL(10,2),
    CONSTRAINT fk_facility FOREIGN KEY (facility_id) REFERENCES green_building_facility(id)
);

CREATE TABLE water_meter (
    meter_id INT PRIMARY KEY,
    facility_id INT,
    meter_number VARCHAR(10),
    CONSTRAINT fk_facility_meter FOREIGN KEY (facility_id) REFERENCES green_building_facility(id)
);

CREATE TABLE monthly_water_usage (
    usage_id INT PRIMARY KEY,
    meter_id INT,
    reading_date DATE,
    water_used DECIMAL(10,2),
    CONSTRAINT fk_meter_usage FOREIGN KEY (meter_id) REFERENCES water_meter(meter_id)
);

CREATE TABLE billing_info (
    billing_id INT PRIMARY KEY,
    facility_id INT,
    billing_date DATE,
    total_amount DECIMAL(10,2),
    CONSTRAINT fk_facility_billing FOREIGN KEY (facility_id) REFERENCES green_building_facility(id)
);
