tables.table_schema:
CREATE TABLE waste_management_project (
    project_id UUID PRIMARY KEY,
    project_name TEXT,
    project_location TEXT,
    waste_type TEXT
);

CREATE TABLE waste_collection (
    collection_id UUID PRIMARY KEY,
    project_id UUID,
    collection_date DATE,
    collection_weight DECIMAL
);

CREATE TABLE disposal_sites (
    site_id UUID PRIMARY KEY,
    site_location TEXT,
    site_capacity DECIMAL
);

CREATE TABLE GreenBuildings (
    building_id INT PRIMARY KEY,
    building_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL
);

CREATE TABLE WasteBins (
    bin_id INT PRIMARY KEY,
    bin_location VARCHAR(255) NOT NULL,
    bin_capacity INT
);

CREATE TABLE WasteTypes (
    type_id INT PRIMARY KEY,
    waste_type VARCHAR(255) NOT NULL,
    recycling BOOLEAN
);

CREATE TABLE PickupSchedule (
    schedule_id INT PRIMARY KEY,
    building_id INT NOT NULL,
    pickup_day VARCHAR(10) NOT NULL
);
