tables.table_schema:
CREATE TABLE green_energy_generation (
    building_id INT PRIMARY KEY,
    building_name TEXT,
    energy_source TEXT,
    generation_capacity FLOAT
);

CREATE TABLE building_installations (
    building_id UUID PRIMARY KEY,
    building_name VARCHAR(255),
    installation_date DATE,
    renewable_energy_source VARCHAR(50)
);

CREATE TABLE energy_generation (
    generation_id UUID PRIMARY KEY,
    building_id UUID REFERENCES building_installations(building_id),
    generation_date TIMESTAMP,
    energy_generated DECIMAL
);

CREATE TABLE green_buildings (
    building_id INT PRIMARY KEY,
    building_name VARCHAR(255),
    location VARCHAR(255),
    online_status BOOLEAN
);

CREATE TABLE EnergyGeneration (
	generation_id INT PRIMARY KEY,
	source_id INT REFERENCES EnergySource(source_id),
	generation_date DATE,
	generation_amount DECIMAL
);

CREATE TABLE BuildingEnergyUsage (
	usage_id INT PRIMARY KEY,
	building_id INT REFERENCES GreenBuilding(building_id),
	usage_date DATE,
	usage_amount DECIMAL
);

CREATE TABLE EnergyConsumption (
	consumption_id INT PRIMARY KEY,
	building_id INT REFERENCES GreenBuilding(building_id),
	consumption_date DATE,
	consumption_amount DECIMAL
);
