tables.table_schema:
CREATE TABLE FaultDetection (
    id INT PRIMARY KEY,
    device_id INT,
    fault_type VARCHAR(50),
    detection_timestamp TIMESTAMP
);

CREATE TABLE OutageManagement (
    id INT PRIMARY KEY,
    device_id INT,
    outage_start_timestamp TIMESTAMP,
    outage_end_timestamp TIMESTAMP
);

CREATE TABLE FaultLog (
    id INT PRIMARY KEY,
    fault_id INT,
    timestamp TIMESTAMP,
    description TEXT
);

CREATE TABLE SmartMeters (
    meter_id UUID PRIMARY KEY,
    customer_id UUID,
    location VARCHAR(255),
    install_date DATE
);

CREATE TABLE PowerLines (
    line_id UUID PRIMARY KEY,
    voltage INT,
    length DECIMAL,
    material VARCHAR(255)
);

CREATE TABLE Faults (
    fault_id UUID PRIMARY KEY,
    line_id UUID,
    timestamp TIMESTAMP,
    description TEXT
);

CREATE TABLE Outages (
    outage_id UUID PRIMARY KEY,
    line_id UUID,
    start_time TIMESTAMP,
    end_time TIMESTAMP
);
