tables.table_schema:
CREATE TABLE TrafficViolations (
  violation_id INT PRIMARY KEY,
  vehicle_id INT,
  violation_type VARCHAR,
  violation_date DATE
);

CREATE TABLE Vehicles (
  vehicle_id INT PRIMARY KEY,
  license_plate VARCHAR,
  owner_name VARCHAR,
  vehicle_type VARCHAR
);

CREATE TABLE Citations (
  citation_id INT PRIMARY KEY,
  violation_id INT,
  vehicle_id INT,
  issued_date DATE
);

CREATE TABLE Traffic_Cameras (
    camera_id UUID PRIMARY KEY,
    location TEXT,
    direction TEXT,
    status TEXT
);

CREATE TABLE Vehicles (
    vehicle_id UUID PRIMARY KEY,
    license_plate TEXT,
    vehicle_type TEXT
);

CREATE TABLE Officers (
    officer_id UUID PRIMARY KEY,
    full_name TEXT,
    badge_number TEXT
);
