tables.table_schema:
CREATE TABLE registered_sex_offenders (
 offender_id INT PRIMARY KEY,
 offender_name VARCHAR(255) NOT NULL,
 offender_address VARCHAR(255) NOT NULL,
 offense_description TEXT,
 registration_date DATE
);

CREATE TABLE offender_restrictions (
 restriction_id INT PRIMARY KEY,
 offender_id INT REFERENCES registered_sex_offenders(offender_id),
 restriction_description TEXT
);

CREATE TABLE offender_photos (
    photo_id UUID PRIMARY KEY,
    offender_id UUID REFERENCES registered_offenders(offender_id),
    photo_url VARCHAR(255),
    upload_date TIMESTAMP
);
