tables.table_schema:
CREATE TABLE restraining_order_officers (
    officer_id INT PRIMARY KEY,
    officer_name VARCHAR(100),
    officer_badge_number VARCHAR(50),
    officer_department VARCHAR(100)
);

CREATE TABLE Individuals (
  id INT PRIMARY KEY,
  name VARCHAR(255),
  restraining_order_nbr VARCHAR(50),
  date_issued DATE
);

CREATE TABLE security_logs (
    log_id INT PRIMARY KEY,
    log_date TIMESTAMP,
    action_taken VARCHAR(255),
    security_personnel VARCHAR(100)
);

CREATE TABLE individuals (
    id INT PRIMARY KEY,
    name STRING,
    address STRING,
    age INT
);

CREATE TABLE relationships (
    id INT PRIMARY KEY,
    individual1_id INT,
    individual2_id INT,
    relationship_type STRING
);

CREATE TABLE security_clearance (
    individual_id INT PRIMARY KEY,
    clearance_level STRING
);

CREATE TABLE incidents (
    incident_id INT PRIMARY KEY,
    incident_date DATE,
    description STRING,
    individuals_involved STRING
);
