tables.table_schema:
CREATE TABLE suspicious_activities (
    activity_id INT PRIMARY KEY,
    activity_name VARCHAR(255),
    activity_type VARCHAR(50),
    activity_timestamp TIMESTAMP
);

CREATE TABLE criminal_investigations (
    case_id INT PRIMARY KEY,
    case_description TEXT,
    suspect_name VARCHAR(255),
    case_status VARCHAR(50)
);

CREATE TABLE CCTV_Footage (
    footage_id INT PRIMARY KEY,
    footage_data BYTES,
    date_recorded TIMESTAMP,
    location VARCHAR(255)
);

CREATE TABLE Suspicious_Activities (
    activity_id INT PRIMARY KEY,
    activity_description VARCHAR(255),
    footage_id INT REFERENCES CCTV_Footage(footage_id),
    timestamp TIMESTAMP
);

CREATE TABLE Criminals (
    criminal_id INT PRIMARY KEY,
    name VARCHAR(255),
    affiliation VARCHAR(255)
);

CREATE TABLE Investigations (
    investigation_id INT PRIMARY KEY,
    activity_id INT REFERENCES Suspicious_Activities(activity_id),
    criminal_id INT REFERENCES Criminals(criminal_id),
    status VARCHAR(50)
);
