tables.table_schema:
CREATE TABLE IF NOT EXISTS WasteCollectionSchedule (
  collection_id INT PRIMARY KEY,
  collection_date DATE,
  pickup_area VARCHAR(50)
);

CREATE TABLE IF NOT EXISTS RouteMapping (
  route_id INT PRIMARY KEY,
  collection_id INT,
  vehicle_id INT,
  driver_id INT
);

CREATE TABLE WasteManagementRoutes (
  route_id INT PRIMARY KEY,
  route_name VARCHAR(50) NOT NULL,
  route_schedule VARCHAR(100) NOT NULL
);

CREATE TABLE WasteManagementTrucks (
  truck_id INT PRIMARY KEY,
  truck_license_plate VARCHAR(20) NOT NULL,
  truck_capacity FLOAT NOT NULL
);

CREATE TABLE WasteManagementPickups (
  pickup_id INT PRIMARY KEY,
  pickup_date DATE NOT NULL,
  pickup_time TIME NOT NULL,
  truck_id INT REFERENCES WasteManagementTrucks(truck_id)
);

CREATE TABLE WasteManagementGarbageBins (
  bin_id INT PRIMARY KEY,
  bin_location VARCHAR(100) NOT NULL,
  bin_capacity FLOAT NOT NULL
);

CREATE TABLE WasteManagementLocation (
    location_id UUID PRIMARY KEY,
    location_name VARCHAR(255) NOT NULL,
    location_address VARCHAR(255) NOT NULL
);

CREATE TABLE WasteCollectionRoute (
    route_id UUID PRIMARY KEY,
    route_name VARCHAR(255) NOT NULL,
    route_distance DECIMAL(10, 2) NOT NULL,
    route_status VARCHAR(50) NOT NULL
);

CREATE TABLE GarbageCollectionWorker (
    worker_id UUID PRIMARY KEY,
    worker_name VARCHAR(255) NOT NULL,
    worker_role VARCHAR(50) NOT NULL
);

CREATE TABLE WorkerAssignment (
    assignment_id UUID PRIMARY KEY,
    worker_id UUID REFERENCES GarbageCollectionWorker(worker_id),
    schedule_id UUID REFERENCES WasteCollectionSchedule(schedule_id)
);
