tables.table_schema:
CREATE TABLE landfill_capacity (
  landfill_id INT PRIMARY KEY,
  landfill_name VARCHAR(255),
  capacity DECIMAL
);

CREATE TABLE LandfillSite (
 id INT PRIMARY KEY,
 site_name VARCHAR(255),
 location VARCHAR(255),
 capacity DECIMAL
);

CREATE TABLE LandfillEmployee (
 id INT PRIMARY KEY,
 employee_name VARCHAR(255),
 position VARCHAR(50),
 salary DECIMAL
);
