tables.table_schema:
CREATE TABLE RecyclingMaterials (
  material_id INT PRIMARY KEY,
  material_name VARCHAR(255),
  category VARCHAR(50),
  recycling_fee DECIMAL(10, 2)
);

CREATE TABLE waste_recycling_process (
  process_id INT PRIMARY KEY,
  process_name TEXT NOT NULL,
  process_type TEXT,
  process_status TEXT
);

CREATE TABLE materials_used (
  material_id INT PRIMARY KEY,
  material_name TEXT NOT NULL,
  material_type TEXT,
  material_quantity INT
);

CREATE TABLE waste_management_operations (
  operation_id INT PRIMARY KEY,
  operation_name TEXT NOT NULL,
  operation_type TEXT
);

CREATE TABLE RecyclingCenter (
  id INT PRIMARY KEY,
  name VARCHAR,
  location VARCHAR
);

CREATE TABLE WasteCategory (
  id INT PRIMARY KEY,
  category_name VARCHAR,
  description TEXT
);

CREATE TABLE Materials (
  id INT PRIMARY KEY,
  material_name VARCHAR,
  material_type VARCHAR,
  recyclable BOOLEAN
);
