tables.table_schema:
CREATE TABLE HazardousWasteDisposal (
  id INT PRIMARY KEY,
  waste_type VARCHAR(50),
  disposal_method VARCHAR(50),
  disposal_date DATE
);

CREATE TABLE ComplianceRegulations (
  id INT PRIMARY KEY,
  regulation_name VARCHAR(100),
  compliance_status BOOLEAN,
  last_inspection DATE
);

CREATE TABLE WasteDisposal (
    id INT PRIMARY KEY,
    waste_type VARCHAR(255),
    disposal_date DATE,
    disposal_location VARCHAR(255)
);

CREATE TABLE ComplianceRegulations (
    id INT PRIMARY KEY,
    regulation_type VARCHAR(255),
    regulation_description TEXT
);

CREATE TABLE WasteRegulations (
    regulation_id UUID PRIMARY KEY,
    regulation_name VARCHAR(255),
    regulator_name VARCHAR(255),
    regulation_description TEXT
);
