tables.table_schema:
CREATE TABLE waste_collection_costs (
  id INT PRIMARY KEY,
  company_name VARCHAR(50),
  collection_date DATE,
  collection_cost DECIMAL(10, 2)
);

CREATE TABLE WasteCollectionCosts (
  id INT PRIMARY KEY,
  company_name VARCHAR(50),
  collection_date DATE,
  cost DECIMAL(10,2)
);

CREATE TABLE CollectionAreas (
  area_id INT PRIMARY KEY,
  area_name VARCHAR(50),
  city VARCHAR(50),
  region VARCHAR(50)
);

CREATE TABLE CostAnalysis (
  cost_id INT PRIMARY KEY,
  analysis_date DATE,
  analysis_type VARCHAR(50),
  cost_factor DECIMAL(10,2)
);

CREATE TABLE OptimizationAreas (
  optimization_id INT PRIMARY KEY,
  area_id INT,
  optimization_date DATE,
  optimization_type VARCHAR(50)
);
