tables.table_schema:
CREATE TABLE WasteFacilities (
  facility_id INT PRIMARY KEY,
  facility_name VARCHAR(50),
  location VARCHAR(100),
  capacity DECIMAL
);

CREATE TABLE EfficiencyMetrics (
  metric_id INT PRIMARY KEY,
  facility_id INT,
  efficiency_score DECIMAL,
  CONSTRAINT fk_efficiency_facility_id FOREIGN KEY (facility_id) REFERENCES WasteFacilities(facility_id)
);

CREATE TABLE WasteTrucks (
    truck_id INT PRIMARY KEY,
    license_plate VARCHAR(15),
    capacity INT,
    status ENUM('Available', 'In Use', 'Under Maintenance')
);

CREATE TABLE WasteCollection (
    collection_id INT PRIMARY KEY,
    truck_id INT,
    collection_date DATE,
    collected_weight DECIMAL(10, 2)
);

CREATE TABLE WasteProcessing (
    processing_id INT PRIMARY KEY,
    facility_id INT,
    processing_date DATE,
    processed_weight DECIMAL(10, 2)
);
