tables.table_schema:
CREATE TABLE waste_collection_events (
    event_id INT PRIMARY KEY,
    event_name VARCHAR(50),
    event_date DATE,
    event_location VARCHAR(100)
);

CREATE TABLE community_clean_up (
    clean_up_id INT PRIMARY KEY,
    clean_up_name VARCHAR(50),
    clean_up_date DATE,
    clean_up_location VARCHAR(100)
);

CREATE TABLE waste_collection_sites (
    site_id INT PRIMARY KEY,
    site_name VARCHAR(255),
    site_address VARCHAR(255),
    site_capacity INT
);
