tables.table_schema:
CREATE TABLE energy_storage_systems ( 
    id INT PRIMARY KEY, 
    system_name VARCHAR(50), 
    capacity DECIMAL(10,2), 
    efficiency FLOAT 
);

CREATE TABLE energy_consumption_logs ( 
    log_id SERIAL PRIMARY KEY, 
    system_id INT REFERENCES energy_storage_systems(id), 
    date_timestamp TIMESTAMP, 
    energy_consumed DECIMAL(10,2) 
);

CREATE TABLE EnergyStorageSystems (
    id SERIAL PRIMARY KEY,
    system_name VARCHAR(255) NOT NULL,
    capacity DECIMAL,
    efficiency DECIMAL
);
