tables.table_schema:
CREATE TABLE nlp_tasks (
    task_id INT PRIMARY KEY,
    task_name VARCHAR(255),
    task_description TEXT
);

CREATE TABLE nlp_results (
    result_id INT PRIMARY KEY,
    task_id INT,
    result_data JSON,
    result_timestamp TIMESTAMP
);

CREATE TABLE NLP_Documents (
    document_id UUID PRIMARY KEY,
    document_text TEXT
);

CREATE TABLE NLP_Tokens (
    token_id UUID PRIMARY KEY,
    token_text TEXT,
    document_id UUID REFERENCES NLP_Documents(document_id)
);

CREATE TABLE NLP_Documents (
    document_id INT PRIMARY KEY,
    document_text TEXT
);

CREATE TABLE NLP_Keywords (
    keyword_id INT PRIMARY KEY,
    keyword_text TEXT
);

CREATE TABLE NLP_Entities (
    entity_id INT PRIMARY KEY,
    entity_text TEXT,
    entity_type TEXT
);

CREATE TABLE texts (
    id INT PRIMARY KEY,
    content TEXT
);

CREATE TABLE tokens (
    id INT PRIMARY KEY,
    text_id INT,
    token TEXT,
    position INT
);

CREATE TABLE metadata (
    id INT PRIMARY KEY,
    text_id INT,
    language TEXT,
    created_at TIMESTAMP
);
